'use strict';

var gui = new dat.GUI();
var canvas = document.querySelector('canvas');
var c = canvas.getContext('2d');
canvas.width = window.innerWidth;
canvas.height = window.innerHeight;
var wave = {
  y: canvas.height / 2,
  length: 0.01,
  amplitude: 100,
  frequency: 0.01
};
var strokeColor = {
  h: 200,
  s: 50,
  l: 50
};
var backgroundColor = {
  r: 0,
  g: 0,
  b: 0,
  a: 0.01
};
var waveFolder = gui.addFolder('wave');
waveFolder.add(wave, 'y', 0, canvas.height);
waveFolder.add(wave, 'length', -0.01, 0.01);
waveFolder.add(wave, 'amplitude', 0, -300, 300);
waveFolder.add(wave, 'frequency', -0.01, 1);
waveFolder.open();
var strokeFolder = gui.addFolder('stroke');
strokeFolder.add(strokeColor, 'h', 0, 255);
strokeFolder.add(strokeColor, 's', 0, 100);
strokeFolder.add(strokeColor, 'l', 0, 100);
strokeFolder.open();
var backgroundFolder = gui.addFolder('background');
backgroundFolder.add(backgroundColor, 'r', 0, 255);
backgroundFolder.add(backgroundColor, 'g', 0, 255);
backgroundFolder.add(backgroundColor, 'b', 0, 255);
backgroundFolder.add(backgroundColor, 'a', 0, 1);
backgroundFolder.open();
var increment = wave.frequency;

function animate() {
  requestAnimationFrame(animate);
  c.fillStyle = "rgba(".concat(backgroundColor.r, ", ").concat(backgroundColor.g, ", ").concat(backgroundColor.b, ", ").concat(backgroundColor.a, ")");
  c.fillRect(0, 0, canvas.width, canvas.height);
  c.beginPath();
  c.moveTo(0, canvas.height / 2);

  for (var i = 0; i < canvas.width; i++) {
    c.lineTo(i, wave.y + Math.sin(i * wave.length + increment) * wave.amplitude * Math.sin(increment));
  }

  c.strokeStyle = "hsl(".concat(strokeColor.h * Math.sin(increment), ", ").concat(strokeColor.s, "%, ").concat(strokeColor.l, "% )");
  c.stroke();
  increment += wave.frequency;
}

animate();